import { Component } from '@angular/core';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  signUpUsers: any[] = [];

  signUpObj: any = {
    username: '',
    email: '',
    password: ''
  };

  loginObj: any = {
    username: '',
    password: ''
  };

  constructor() { }

  ngOnInit(): void {
    const localData = localStorage.getItem('signUpUsers');
    if (localData) {
      this.signUpUsers = JSON.parse(localData);
    }
  }

  onSignUp() {
    this.signUpUsers.push(this.signUpObj);
    localStorage.setItem('signUpUsers', JSON.stringify(this.signUpUsers));
    this.signUpObj = {
      username: '',
      email: '',
      password: ''
    };
  }

  onLogin() {
    const userExist = this.signUpUsers.find(user => user.email === this.loginObj.email && user.password === this.loginObj.password );
    if (userExist != undefined) {
      alert('Login Success');
    }
    else {
      alert('Login Failed');
    }
  }
}
<div class="container">
    <div>
        <h2>Sign Up</h2>

        <a routerLink="/" class="back-link">< Back</a>
        <form class="signup-form" (click)="onSignUp()">

            <label for="username">Username:</label>
            <input type="text" id="username" name="username" required><br>

            <label for="email">Email:</label>
            <input type="email" id="email" name="email" required><br>

            <label for="password">Password:</label>
            <input type="password" id="password" name="password" required><br>

            <label for="confirm-password">Confirm Password:</label>
            <input type="password" id="confirm-password" name="confirm-password" required><br>

            <button type="submit" class="signup-button">Sign Up</button>
            <button type="button" (click)="onCancel()" class="cancel-button">Cancel</button>
        </form>
    </div>
</div>
